!function(){window.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("[data-qr-code]"),t=document.querySelector("[data-qr-number]"),n=document.querySelector("[data-qr-success"),d={hidden:"hidden"};new QRCode(e,{text:e.dataset.identifier,width:120,height:120,imageAltText:theme.strings.qrImageAlt}),t.addEventListener("focus",e=>{e.target.select()}),document.querySelector("[data-gift-card-copy]").addEventListener("click",()=>{navigator.clipboard.writeText(t.value).then(function(){n.classList.remove(d.hidden)})})})}();